WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/15.0/connection.js")} 

var ids = {!GETRECORDIDS($ObjectType.Case)};

if(ids.length < 1) {
alert('Please select one Case record.');
}

if(ids.length >1){
alert('Please select only one Case record.');
}

if(ids.length ==1){

var result = sforce.connection.query("Select id, ContactId from Case where id='"+ids[0]+"'");
var records = result.getArray("records"); 

if (records.length < 1){
alert('No Record Found.');
}
else{
var wnd = window.open('/apex/synety__ContactPhone?id='+records[0].ContactId+'&returnURL=003','Make_Call','scrollbars=0,width=550,height=400,left=370,top=170');
wnd.focus();
}
}
